package com.ccic.salesapp.eureka;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.cloud.netflix.eureka.server.EnableEurekaServer;@SpringBootApplication@EnableEurekaServerpublic class EurekaAppStart {    public static void main(String[] args) {        SpringApplication.run(EurekaAppStart.class,args);    }}