mapper.select:
    SELECT C.CONFIG_VALUE FROM t_noncar_general_config C 
    WHERE C.VALID_STATUS='1' 
    
		AND C.COM_CODE=#{comCode}
	
	 
		AND 	C.RISK_CODE=#{riskCode}
	
    AND C.CONFIG_TYPE= #{configType}

