mapper.update:
update t_support_org_branch
set org_code = #{orgCode,jdbcType=VARCHAR},
  up_org_code = #{upOrgCode,jdbcType=VARCHAR},
  up_branch = #{upBranch,jdbcType=CHAR},
  deep = #{deep,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, org_code, up_org_code, up_branch, deep
from t_support_org_branch
where id = #{id,jdbcType=INTEGER}
