mapper.select:
		select `id`,`user_code`,`customer_total`,`capacity`,`channel` from t_performance_customer_standard

		  		   and id = #{id} 
		  		   and user_code = #{userCode} 
		  		   and customer_total = #{customerTotal} 
		  		   and capacity = #{capacity} 
		  		   and channel = #{channel} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from t_performance_customer_standard

  		   and id = #{id} 
  		   and user_code = #{userCode} 
  		   and customer_total = #{customerTotal} 
  		   and capacity = #{capacity} 
  		   and channel = #{channel}

SELECT 
`id`,`user_code` userCode,`customer_total` customerTotal,`capacity`,`channel`
from t_performance_customer_standard where USER_CODE=#{userCode}
