mapper.update:
update t_performance_commplan set

	WITHDRAWALSMSG=#{withdrawalsMsg},


	WITHDRAWALSFLAG=#{withdrawalsFlag}

where CERTIID = #{certiId} and AGENTCODE=#{agentCode}

mapper.select:
SELECT SUM(IFNULL(T.PLANFEE,0)) PACKSUMFEE,T.PACKNO,MIN(T.PACKAGEDATE) PACKAGEDATE FROM t_performance_commplan T WHERE T.AGENTCODE=#{agentCode}
AND EXISTS ( SELECT 1 FROM t_performance_commplan P WHERE P.AGENTCODE = #{agentCode} AND P.HANDLER1CODE = #{handler1Code} AND P.BILLNO=T.BILLNO )
AND T.PACKNO IS NOT NULL AND T.PACKAGEDATE >= str_to_date(#{startDay},'%Y-%m-%d') AND T.PACKAGEDATE < DATE_ADD(str_to_date(#{endDay},'%Y-%m-%d'),INTERVAL 1 DAY) GROUP BY T.PACKNO ORDER BY MIN(T.PACKAGEDATE) DESC, T.PACKNO DESC
limit #{offset}, #{limit}

select count(*) from ( SELECT SUM(IFNULL(T.PLANFEE,0)) PACKSUMFEE,T.PACKNO,MIN(T.PACKAGEDATE) PACKAGEDATE FROM t_performance_commplan T WHERE T.AGENTCODE=#{agentCode}
AND EXISTS ( SELECT 1 FROM t_performance_commplan P WHERE P.AGENTCODE = #{agentCode} AND P.HANDLER1CODE = #{handler1Code} AND P.BILLNO=T.BILLNO )
AND T.PACKNO IS NOT NULL AND T.PACKAGEDATE >= str_to_date(#{startDay},'%Y-%m-%d') AND T.PACKAGEDATE < DATE_ADD(str_to_date(#{endDay},'%Y-%m-%d'),INTERVAL 1 DAY) GROUP BY T.PACKNO ORDER BY MIN(T.PACKAGEDATE) DESC, T.PACKNO DESC ) t

SELECT T.CERTIID CERTIID_F,T.* FROM t_performance_commplan T
WHERE
T.AGENTCODE=#{agentCode}

	AND EXISTS (
	SELECT 1 FROM t_performance_commplan P
	WHERE
	P.AGENTCODE = #{agentCode}
	AND P.HANDLER1CODE = #{handler1Code}
	AND
	P.BILLNO=T.BILLNO
	)

AND T.PACKNO IS NOT NULL
AND T.PACKNO=#{packNo}
ORDER BY T.CERTIID

	limit #{offset}, #{limit}

select count(1) from (
SELECT T.CERTIID CERTIID_F,T.* FROM
t_performance_commplan T
WHERE T.AGENTCODE=#{agentCode}

	AND EXISTS (
	SELECT 1 FROM t_performance_commplan P
	WHERE
	P.AGENTCODE = #{agentCode}
	AND P.HANDLER1CODE = #{handler1Code}
	AND
	P.BILLNO=T.BILLNO
	)

AND T.PACKNO IS NOT NULL
AND T.PACKNO=#{packNo}
ORDER BY T.CERTIID
) t
